import styled from 'styled-components';
import { useQuery } from '@apollo/client';
import Team from './Team';
import gql from "graphql-tag";
import { perPage } from '../config';

export const ALL_TEAMS_QUERY = gql`
    query ALL_TEAMS_QUERY($skip: Int = 0, $first: Int) {
        allTeams(first: $first, skip: $skip){
            id
            team_id
            team_name
            team_first_color
        }
    }
`;

const Center = styled.div`
	text-align: center;

	.buttonup {
	    width: auto;
	    color: white;
	    background-color: ${props => props.theme.orange};
	    border: 0;
	    border-radius: ${props => props.theme.roundedCorner} !important;
	    font-size: 1rem;
	    padding: 0.9rem 1.2rem;
  	}
`;

const ItemsList = styled.div`
	display: grid;
	grid-template-columns: auto auto auto auto;
	grid-gap: 60px;
	max-width: ${props => props.theme.maxWidth};
	margin: 0 auto;
	@media (max-width: 830px) {
		grid-template-columns: 1fr 1fr;
		grid-gap: 0%;
		a {
    		font-size: 1.2rem;
		}
	}
	@media (max-width: 500px) {
		grid-template-columns: 1fr;
		grid-gap: 0%;
	}

    .country-colors {
        height: 2rem;
        width: 2rem;
        display: inline-flex;
        border-radius: 50px;
        vertical-align: top;
    }
`;

export default function Teams ({ page }) {
    const { data, error, loading } = useQuery(ALL_TEAMS_QUERY, {
        variables: {
            skip: page * perPage - perPage,
            first: perPage,
        },
    });
    console.log( data, error, loading )
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error.message}</p>;
    return (
        <Center>
            <ItemsList>
                {data.allTeams.map((team) => (
                    <Team key={team.id} team={team} />
                ))}
            </ItemsList>
        </Center>
    );
}
